void draw_ship(ship* player)
{
    // check dx and dy

    int x = player->x;
    int y = player->y;
    int dx = player->dx;
    int dy = player->dy;

    counter = 0;

    if (dx == 0 && dy == 1) // ship Up
    {                       // Drawing ship up case

        for (int i = y; i < y + SHIPUP_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPUP_WIDTH; j++)
            {
                if (shipUp[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPUP_WIDTH / 2), i - (SHIPUP_HEIGHT / 2), shipUp[counter]);
                counter++;
            }
        }
    }
    else if (dx == 1 && dy == 0)
    { // ship Right
        for (int i = y; i < y + SHIPRIGHT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPRIGHT_WIDTH; j++)
            {
                if (shipRight[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPRIGHT_WIDTH / 2), i - (SHIPRIGHT_HEIGHT / 2), shipRight[counter]);
                counter++;
            }
        }
    }
    else if (dx == 0 && dy == -1)
    { // ship Down
        for (int i = y; i < y + SHIPDOWN_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPDOWN_WIDTH; j++)
            {
                if (shipDown[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPDOWN_WIDTH / 2), i - (SHIPDOWN_HEIGHT / 2), shipDown[counter]);
                counter++;
            }
        }
    }
    else if (dx == -1 && dy == 0)
    {
        for (int i = y; i < y + SHIPLEFT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPLEFT_WIDTH; j++)
            {
                if (shipLeft[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPLEFT_WIDTH / 2), i - (SHIPLEFT_HEIGHT / 2), shipLeft[counter]);
                counter++;
            }
        }
    }
    else if (dx == 1 && dy == 1) // up Right
    {
        for (int i = y; i < y + SHIPUPRIGHT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPUPRIGHT_WIDTH; j++)
            {
                if (shipUpRight[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPUPRIGHT_WIDTH / 2), i - (SHIPUPRIGHT_HEIGHT / 2), shipUpRight[counter]);
                counter++;
            }
        }
    }
    else if (dx == -1 && dy == 1)
    { // upLeft
        for (int i = y; i < y + SHIPUPLEFT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPUPLEFT_WIDTH; j++)
            {
                if (shipUpLeft[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPUPLEFT_WIDTH / 2), i - (SHIPUPLEFT_HEIGHT / 2), shipUpLeft[counter]);
                counter++;
            }
        }
    }
    else if (dx == -1 && dy == -1)
    { // down Left
        for (int i = y; i < y + SHIPDOWNLEFT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPDOWNLEFT_WIDTH; j++)
            {
                if (shipDownLeft[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPDOWNLEFT_WIDTH / 2), i - (SHIPDOWNLEFT_HEIGHT / 2), shipDownLeft[counter]);
                counter++;
            }
        }
    }
    else if (dx == 1 && dy == -1)
    { // down Right
        for (int i = y; i < y + SHIPDOWNRIGHT_HEIGHT; i++)
        {
            for (int j = x; j < x + SHIPDOWNRIGHT_WIDTH; j++)
            {
                if (shipDownRight[counter] == 0x0000)
                {
                    counter++;
                    continue;
                }
                draw_pixel(j - (SHIPDOWNRIGHT_WIDTH / 2), i - (SHIPDOWNRIGHT_HEIGHT / 2), shipDownRight[counter]);
                counter++;
            }
        }
    }
}
